INSERT INTO `wp_posts` VALUES (8, 1, '2012-05-16 06:20:53', '2012-05-16 06:20:53', '<!--:fr--><h2>Une Fondation au service du bien-être de l\'enfance</h2>\r\nLa vocation de la <strong>fondation d’entreprise Bel</strong> est de soutenir, en France et à l’international, des initiatives en faveur de <strong>l’enfance et de son bien-être</strong> essentiellement par la promotion de <strong>modes d\'alimentation équilibrés</strong><strong></strong>.<!--:--><!--:en--><h2>A Foundation which serves the well-being of children</h2>\r\n<strong>The Bel Foundation</strong> acts on behalf of <strong>children</strong> and their <strong>well-being</strong>around the world, by promoting a <strong>balanced diet</strong> and <strong>preserving the environment</strong>, as far as it is required for a <strong>healthy diet</strong>.<!--:-->', '<!--:fr-->La Fondation<!--:en-->The Fondation<!--:-->', '', 'publish', 'open', 'open', '', 'la-fondation', '', '', '2012-07-12 11:43:13', '2012-07-12 10:43:13', '', 0, 'http://localhost/wordpress/2012/05/bel/?page_id=8', 0, 'page', '', 0) ; 
